<?php//日志接口class BuyApi extends Api{	public function joinTuan(){		$tid	  = $_REQUEST['tid'];		if ($user = service('Passport')->getLocalUser($_REQUEST['email'], $_REQUEST['password'])) {			if ($user['is_active'] == 0) {				$this->redirect(U('home/Public/login'));			}			$this->setSessionAndCookie($user['uid'], $user['uname'], $user['email'], intval($_POST['remember']) === 1);		    redirect(__ROOT__."/apps/buy/deal.php?id=".$tid."&from=in");		 }	}	private function setSessionAndCookie($uid, $uname, $email, $remember = false) {        $_SESSION['mid']    = $uid;        $_SESSION['uname']  = $uname;        $remember ?		cookie('LOGGED_USER',jiami('thinksns.'.$uid),(3600*24*365)) :		cookie('LOGGED_USER',jiami('thinksns.'.$uid),(3600*2));    }}