<?php//个人主页接口class PersonalPageApi extends Api{	//查看用户信息	public function profile(){        $res = D('User','home')->getUserByIdentifier($this->data['uid']);        if($res == false){            return 0;        }else{        	$res['score'] = $res['credit']['score']['credit'];            $res['experience'] = $res['credit']['experience']['credit'];        	//删除无用字段        	unset($res['province'],$res['city'],$res['credit'],$res['admin_level'],$res['commend'],$res['is_init'],$res['is_synchronizing'],$res['identity'],$res['myop_menu_num'],$res['api_key']);            return $res;        }	}	//我关注的	public function following(){        return D('WeiboApi','weibo')->friends_timeline( $this->mid , $this->data['since_id'], $this->max_id , $this->count , $this->page ) ;	}	// 群组动态	public function group(){		if(!$this->data['gid']) return 0;        return D('WeiboOperate','group')->getHomeList($this->mid, $this->data['gid'], 'index', $this->data['max_id'], $this->data['row']);	}	//正在发生	public function lastest(){        return D('WeiboApi','weibo')->public_timeline( $this->since_id , $this->max_id , $this->count , $this->page ) ;	}}